import React, { Fragment, useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { update } from "../../lib/actions"

const Row = (props) => {
  const { quantity, details } = props.item
  const dispatch = useDispatch();

  const add = (item, quantity) => {
    dispatch(addToCart(item, quantity))
  }

    return (
      <tr>
        <td>
          <img
            width="70"
            height="70"
            src={process.env.PUBLIC_URL + `/assets/${details.category}/${details.image}`}
            alt={details.name}
          />
        </td>
        <td>{details.name}</td>
        <td>{details.price} {details.unit}</td>
        <td>
          <div className="btn-group" role="group" aria-label="Basic example">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => {
                if (qty > 1) {update('decrement') }
              }}
              >
              -
            </button>
            <span className="btn btn-light">{quantity}</span>
            <button
              type="button"
              className="btn btn-secondary"
                onClick={() =>  {update('increment')}}
              >
              +
            </button>
          </div>
        </td>
        <td>€</td>
        <td>€{(quantity * details.price).toFixed(2)}</td>
        <td>
          <button
            type="button"
            className="btn btn-danger remove"
            //  onClick={() => remove(id)}
             >
            X
          </button>
        </td>
      </tr>
    );
}


const Table = () => {
  const items = useSelector(state => state.items)
  useEffect(() => {
    console.log(`tu as ${items.length} dans le cart!`)

  })
    return (
      <table>
        <tr>
          <th width="200">Product</th>
          <th width="80">Reference</th>
          <th width="150">Price</th>
          <th width="150">Quantity</th>
          <th width="200">Total</th>
        </tr>
        {items.map(item => {
          return(<Row item={item}/>)
        })}
      </table>
    );
  }

const CartPage = () => {
    return (
        <Fragment>
            <div className="container">
                <div className="row">
                    <div className="col-sm cart">
                        <Table /*items={items}*/     />
                    </div>
                    <div className="col-sm-3 order-summary">
                        <ul className="list-group">
                            <li className="list-group-item">Order Summary</li>

                            <li className="list-group-item">
                                <ul className="list-group flex">
                                    <li className="text-left">Subtotal</li>
                                    <li className="text-right">€</li>

                                    {/* <li className="text-right">€{subTotal.toFixed(2)}</li> */}
                                </ul>
                                <ul className="list-group flex">
                                    <li className="text-left">shipping</li>
                                    <li className="text-right">€</li>
                                    {/* <li className="text-right">€{shipping.toFixed(2)}</li> */}
                                </ul>
                                <ul className="list-group flex">
                                    <li className="coupon crimson">
                                        <small>Add Coupon Code</small>
                                    </li>
                                </ul>
                            </li>

                            <li className="list-group-item ">
                                <ul className="list-group flex">
                                    <li className="text-left">Total</li>
                                    <li className="text-right">€</li>
                                    {/* <li className="text-right">€{subTotal == 0.00 ? "0.00" : total.toFixed(2)}</li> */}
                                </ul>
                            </li>
                        </ul>
                        {/* <Link to="/checkout" className={`white btn btn-light btn-lg btn-block checkout ${!items.length && 'disabled'} bg-crimson`}> */}
                        <Link to="/checkout" className={`white btn btn-light btn-lg btn-block checkout bg-crimson`}>
                            Checkout
                      </Link>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default CartPage;
