import React, { Fragment, useState } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import Navbar from "../../components/NavBar";
import Footer from "../../components/Footer";
import CartPage from "./Cart";
import Home from "./Home";
import "../../styles/app.css";
import { list } from "../../data"


const App = props => {
  const { items, onAddToCart, onUpdateCart  } = props
  const [count, setCount] = useState(0);
  const [category, setCategory] = useState(0);
  const [isFiltering, setFiltering] = useState(false);
  const [isFiltered, setFilted] = useState(false);

  const filterResults = (input) => {
    let fullList = list.flat();
    let results = fullList.filter(item => {
      const name = item.name.toLowerCase();
      const tern = input.toLowerCase();
      return name.indexOf(tern) > -1
    })
    setFilted(results)
  }

  const add = (item, quantity) => {
    onAddCart()

  }

  const update = () => {

  }

  return (
    <Fragment>
      <Router>
        <Navbar filter={filterResults} setFiltering={setFiltering} count={count} />
        {/* routes */}
        <Route exact path="/" component={() =>
          <Home
            category={category}
            loadCategory={(i) => { setCategory(i) }}
            addToCart={add}
            updateCart={update}
            list={list}
            isFiltered={isFiltered}
            isFiltering={isFiltering}
          />}
        />
        <Route path="/cart" component={CartPage} />
      </Router>
      <Footer />
    </Fragment>
  );
}
export default App;
