import React, { createContext, useState } from 'react';

const UserProfilContext = createContext({
    firstName: "",
    lastName: "",
    email: "",
    address: "",
    zipCode: "",
    city: "",
    setUserProfilContext: info => {}
})

const UserProfilContextProvider = ({ children }) => {
    const userProfileState = {
        firstName: "",
        lastName: "",
        email: "",
        address: "",
        zipCode: "",
        city: "",
        setUserProfilContext: info => 
            setUserProfile(prevState => ({
                ...prevState,
                firstName: info.firstName,
                lastName: info.lastName,
                email: info.email,
                address: info.address,
                zipCode: info.zipCode,
                city: info.city
        }))
    }

    const [userProfile , setUserProfile] = useState(userProfileState)
    return (<UserProfilContext.Provider value={userProfile}> { children } </UserProfilContext.Provider>)
}

export default UserProfilContextProvider;
