import React, { Fragment, useState } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import Cart from "../pages/Cart";
import Home from "../pages/Home";
import Navbar from "../components/NavBar";
import Footer from "../components/Footer";

import "../styles/app.css";
import { list } from "../../app/data"

const App = () => {
  const [count, setCount] = useState(0);
  const [category, setCategory] = useState(0);
  const [isFiltering, setFiltering] = useState(false);
  const [isFiltered, setFilted] = useState(false);

  const filterResults = (input) => {
    let fullList = list.flat();
    let results = fullList.filter(item => {
      const name = item.name.toLowerCase();
      const tern = input.toLowerCase();
      return name.indexOf(tern) > -1
    })
    setFilted(results)
  }

  return (
    <Fragment>
      <Router>
        <Navbar filter={filterResults} setFiltering={setFiltering} count={count} />
        {/* Routes */}
        <Route exact path="/" components={() => <Home
          category={category}
          loadCategory={setCategory}
          addToCart={setCount}
          list={list}
          isFiltered={isFiltered}
          isFiltering={isFiltering}
          count = {count}
          />} 
        />
        <Route path="/cart" components={Cart} />
      </Router>
      <Footer />
    </Fragment>

  );
}
export default App;
